mapper.select:
SELECT COUNT(*) FROM tbl_challenge
AND (challenge_title LIKE CONCAT('%', #{searchKeyword}, '%') OR challenge_content LIKE CONCAT('%', #{searchKeyword}, '%'))
AND challenge_category_code = #{categoryCode}

SELECT challenge_code, created_at, challenge_title, challenge_content, host_user, recruitment_num, start_date, end_date, xp, thumbnail_url, challenge_category_code, challenge_status FROM tbl_challenge
AND (challenge_title LIKE CONCAT('%', #{searchKeyword}, '%') OR challenge_content LIKE CONCAT('%', #{searchKeyword}, '%'))
AND challenge_category_code = #{categoryCode}
ORDER BY created_at DESC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*)
FROM tbl_challenge_part
WHERE challenge_code = #{challengeCode}
